<?phprequire('includes/application_top.php');// if the customer is not logged on, redirect them to the login pageif (!tep_session_is_registered('customer_id')) {    $navigation->set_snapshot();    tep_redirect(tep_href_link(FILENAME_LOGIN, '', 'SSL'));}// if there is nothing in the customers cart, redirect them to the shopping cart pageif ($cart->count_contents() < 1) {    tep_redirect(tep_href_link(FILENAME_SHOPPING_CART));}// if no shipping method has been selected, redirect the customer to the shipping method selection pageif (!tep_session_is_registered('shipping')) {    tep_redirect(tep_href_link(FILENAME_CHECKOUT_SHIPPING, '', 'SSL'));}// avoid hack attempts during the checkout procedure by checking the internal cartIDif (isset($cart->cartID) && tep_session_is_registered('cartID')) {    if ($cart->cartID != $cartID) {        tep_redirect(tep_href_link(FILENAME_CHECKOUT_SHIPPING, '', 'SSL'));    }}require(DIR_WS_INCLUDES . 'template_top.php');require_once 'includes/languages/' . $_SESSION["language"] . '/modules/payment/pi_ratepay_rechnung.php';require_once 'includes/modules/payment/pi_ratepay_rechnung.php';$ratepay = new pi_ratepay_rechnung();$min = $ratepay->min;$max = $ratepay->max;?><script type="text/javascript">    function submitTerms(){        if(document.getElementById('conditions').checked == true){            document.getElementById('terms').submit();        }        else{            document.getElementById('error').style.display = 'block';        }    }</script><div style="margin: 10px; color: #2F2F2F;">    <table>        <tr>            <td colspan="2">                <img src="images/ratepay_rechnung_rgb.png" alt="RatePAY Rechnung" />                <br/>                <div style="color: red; display: none;" id="error">                    <?php echo PI_RATEPAY_RECHNUNG_AGB_ERROR; ?>                </div>                <br/>                <b>                    <span style="color: red;"><?php echo STORE_NAME; ?></span>                    <?php echo PI_RATEPAY_RECHNUNG_INFO_1; ?>                 </b>                <br/>                <br/>                <?php echo PI_RATEPAY_RECHNUNG_INFO_2; ?>                <b><?php echo PI_RATEPAY_RECHNUNG_INFO_3; ?><?php echo $min; ?> EUR </b>                und                 <b><?php echo PI_RATEPAY_RECHNUNG_INFO_4; ?><?php echo $max; ?> EUR</b>                <br/>                <?php echo PI_RATEPAY_RECHNUNG_INFO_5; ?>                 <br/>                <br/>                <?php echo PI_RATEPAY_RECHNUNG_INFO_6; ?>            </td>        </tr>        <tr>            <td colspan='2'>                <br/>            </td>        </tr>        <tr>            <td colspan='2'>                <table>                    <tr>                        <td style="vertical-align: top;">                            <form action="checkout_confirmation.php?osCsid=<?php echo tep_session_id() ?>" name="terms" method="POST" id="terms">                                <input id="conditions" type="checkbox" name="conditions" value="conditions" id="conditions"/>                                <input type="hidden" value="<?php echo $_GET['coupon']; ?>" name="coupon" id="coupon">                            </form>                        </td>                        <td>                            <?php echo PI_RATEPAY_RECHNUNG_INFO_7; ?>                            <a href="<?php echo $gtc; ?>" target="_blank" style="text-decoration: underline; color: #1E7EC8;">                                <?php echo PI_RATEPAY_RECHNUNG_INFO_8; ?>                            </a>                            <?php echo PI_RATEPAY_RECHNUNG_INFO_9; ?>                            <a href="<?php echo $privacy; ?>" target="_blank" style="text-decoration: underline; color: #1E7EC8;">                                <?php echo PI_RATEPAY_RECHNUNG_INFO_10; ?>                            </a>                                <?php echo PI_RATEPAY_RECHNUNG_INFO_11; ?>                            <?php if ($merchantPrivacy): ?>                                <?php echo PI_RATEPAY_RECHNUNG_INFO_12; ?>                                <a href="<?php echo $merchantPrivacy; ?>" target="_blank" style="text-decoration: underline; color: #1E7EC8;">                                    <?php echo PI_RATEPAY_RECHNUNG_INFO_13; ?>                                </a>.                            <?php endif; ?>                        </td>                    </tr>                </table>            </td>        </tr>        <tr>            <td style="text-align: left;"><a href="checkout_payment.php?osCsid=<?php echo tep_session_id() ?>"><?php echo tep_draw_button(PI_RATEPAY_RECHNUNG_CHECKOUT_BACK, 'triangle-1-e', null, 'primary'); ?></a></td>            <td style="text-align: right;"><a href="javascript:submitTerms();"><?php echo tep_draw_button(PI_RATEPAY_RECHNUNG_CHECKOUT_CONTINUE, 'triangle-1-e', null, 'primary'); ?></a></td>        </tr>    </table></div><?phprequire(DIR_WS_INCLUDES . 'template_bottom.php');require(DIR_WS_INCLUDES . 'application_bottom.php');?>